import React, { useContext } from 'react';
import { Context } from '../index';

const TypeBar = () => {
    const {device} = useContext(Context)
    return (
        <ListGroup>
        {device.brands.map(type =>
            <ListGroup.Item
                style={{cursor: 'pointer'}}
                active={brands.id === device.selectedType.id}
                onClick={() => device.setSelectedType(brands)}
                key={brands.id}
            >
                {brands.name}
            </ListGroup.Item>
        )}
    </ListGroup>
    );
};

export default TypeBar;
